import React, { Component, Fragment } from 'react';

class TimeSet extends Component {
  state = {
    hour: 0,
    min: 0,
    sec: 0,
    miliSec:0,
    inputTime:0
  }

  // 인풋값의 숫자를 타이머에 셋팅
  timeChange = (e) => {
    if ( e.target.value > 60 ) {
      this.setState({
        min: ( e.target.value / 60 ),
        sec: ( e.target.value % 60)
      });
    }else {
      this.setState({
        sec: e.target.value
      });
    }
  }

  // 스타트 버튼 이벤트
  timerStart = (e) => {
    this.setState({
    });
  }

  render() {
    return (
      <Fragment>
        {this.state.hour} : {this.state.min} : {this.state.sec} : {this.state.miliSec}
        <div className="input-row">
          <input type="text" value={this.state.inputTime} onChange={this.timeChange} />
        </div>
        <div className="btn-row">
          <button className="start" onClick={this.timerStart}>start</button>
          <button className="reset" onClick={this.timerReset}>reset</button>
        </div>
      </Fragment>
    );
  }
}

export default TimeSet;
