import React, { Component, Fragment } from 'react';

class TimeSet extends Component {
  state = {
    hour: 0,
    min: 0,
    sec: 0,
    miliSec: 0,
    inputTime: 0
  }

  // 인풋값의 숫자를 타이머에 셋팅
  timeChange = (e) => {
    var now = new Date().getTime();
    var countDownDate = new Date( new Date().getTime() + Math.floor(e.target.value) ).getTime();
    console.log('countDownDate : ', countDownDate);
    console.log('now : ', now);
    var distance = countDownDate - now;
    console.log(distance);
    this.setState({
      inputTime: e.target.value,
      hour: Math.floor( (distance %(1000*60*60*24)) / (1000*60*60) ),
      min: Math.floor( (distance %(1000*60*60*24)) / (1000*60) ),
      sec: Math.floor( (distance %(1000*60*60*24)) / 1000 )
    });
  }

  // 스타트 버튼 이벤트
  timerStart = (e) => {
    this.setState({
    });
  }

  render() {
    return (
      <Fragment>
        {this.state.hour} : {this.state.min} : {this.state.sec} : {this.state.miliSec}
        <div className="input-row">
          <input type="text" value={this.state.inputTime} onChange={this.timeChange} />
        </div>
        <div className="btn-row">
          <button className="start" onClick={this.timerStart}>start</button>
          <button className="reset" onClick={this.timerReset}>reset</button>
        </div>
      </Fragment>
    );
  }
}

export default TimeSet;
