import React, { Component, Fragment } from 'react';

class TimeSet extends Component {
  state = {
    hour: 0,
    min: 0,
    sec: 0,
    milliSec: 0,
    inputTime: '',
    stating: false  // 정지상태
  }

  constructor(props) {
    super(props);

    let _milliSec = 99, _sec, _min, _hour;
    
    if( this.state.stating === false && _milliSec != 99 ){
      clearInterval(interval);
    }
    time = Math.floor(time);

    _milliSec--;  // 밀리세컨드 카운트다운 실행
    _sec = time;  // 입력받은 시간을 초에 입력
    _min = Math.floor(_sec / 60); // 입력받은 시간(_sec)을 분으로 계산
    _sec = _sec % 60; // 상단에서 분 입력이 끝났으므로 계산 후 남은 초를 입력
    _hour = Math.floor(_min / 60);  // 분이 60분을 넘은 경우 시간으로 입력
    _min = _min % 60;
    _hour = _hour % 24;

    // 인풋값의 숫자를 타이머에 셋팅
    let timeChange = (e) => {
      let val = Math.floor(e.target.value);
      let _sec = val % 60;
      let _min = val / 60;

      this.setState({
        inputTime: val,
        sec: Math.floor(_sec),
        min: Math.floor(_min % 60),
        hour: Math.floor(_min / 60),
      });
    }
  }

  timeUpdate = (time) => {
    this.setState({
      sec: Math.floor(time % 60),
      min: Math.floor((time/60) % 60),
      hour: Math.floor((time/60) / 60)
    });
  }

  // 스타트 버튼 이벤트
  timerStart = (e) => {
    let time = document.getElementById('time').value;
    let _milliSec = 99, _sec, _min, _hour;

    let interval = setInterval(() => {  // 0.001초 마다 실행되게 셋팅
      if( this.state.stating === false && _milliSec != 99 ){
        clearInterval(interval);
      }
      time = Math.floor(time);

      _milliSec--;  // 밀리세컨드 카운트다운 실행
      _sec = time;  // 입력받은 시간을 초에 입력
      _min = Math.floor(_sec / 60); // 입력받은 시간(_sec)을 분으로 계산
      _sec = _sec % 60; // 상단에서 분 입력이 끝났으므로 계산 후 남은 초를 입력
      _hour = Math.floor(_min / 60);  // 분이 60분을 넘은 경우 시간으로 입력
      _min = _min % 60;
      _hour = _hour % 24;

      if ( _milliSec < 0 ) {
        _milliSec = 99
        time--;
        if ( time < 0) {
          time = 0;
          _milliSec = 0;
          this.setState({
            stating: false
          })
          clearInterval(interval);
        }
      }else {
        this.setState({
          milliSec: _milliSec,
          sec: _sec,
          min: _min,
          hour: _hour,
          stating: true
        });
      }
    },1);
  }

  timerStop = () => {
    
    this.setState({

    });
  }

  handleChange = (e) => {
    this.setState({
      [e.target.inputTime]: e.target.value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.onCreate(this.state);
    this.setState({
      milliSec: _milliSec,
      sec: _sec,
      min: _min,
      hour: _hour,
      stating: true
    });
  }

  render() {
    return (
      <Fragment>
        {
          this.state.hour < 10 ?
          '0' + this.state.hour : this.state.hour
        } : 
        {
          this.state.min < 10 ?
          '0' + this.state.min : this.state.min
        } : 
        {
          this.state.sec < 10 ?
          '0' + this.state.sec : this.state.sec
        } : 
        {
          this.state.milliSec < 10 ?
          '0' + this.state.milliSec : this.state.milliSec
        }
        <div className="input-row">
          <input type="text" id="time" value={this.state.inputTime} placeholder='0' onChange={this.props.timeChange} />
        </div>
        <div className="btn-row">
          {
            this.state.stating === false
              ? (<button className="start" onClick={this.timerStart}>start</button>)
              : (<button className="stop" onClick={this.timerStop}>stop</button>)
          }
          <button className="reset" onClick={this.timerReset}>reset</button>
        </div>
      </Fragment>
    );
  }
}

export default TimeSet;
