    }
  }

  render() {
    const { hour, min, sec, milliSec, inputTime, stating } = this.state;
    return (
      <article>
        <TimeSet />
        <TimeWrap
          hour={hour}
          min={min}
          sec={sec}
          milliSec={milliSec}
          inputTime={inputTime}
         />
        <InputBox
          onInput={this.handleInput}
        />
        <Buttons
          onStart={this.handleStart}
        />
      </article>
    );
  }
}

export default ComponentWrap;
