import React, { Component, Fragment } from 'react';

class TimeSet extends Component {

  render() {
    return (
      <Fragment>
        {this.props.hour} : {this.props.min} : {this.props.sec} : {this.props.miliSec}
        <div className="input-row">
          <input type="text" value={this.state.sec} onChange={this.TimeChange} />
        </div>
        <div className="btn-row">
          <input type="button" className="start" onClick={this.timerStart}>▶</input>
          <input type="button" className="reset" onClick={this.timerReset}>☜</input>
        </div>
      </Fragment>
    );
  }
}

export default TimeSet;
