import React, { Component, Fragment } from 'react';

class TimeSet extends Component {
  state = {
    hour: '00',
    min: '00',
    sec: '00',
    miliSec: '00',
    inputTime: ''
  }

  // 인풋값의 숫자를 타이머에 셋팅
  timeChange = (e) => {
    let val = Math.floor(e.target.value);
    this.setState({
      inputTime: val,
      hour: Math.floor( val / 3600 ),
      min: Math.floor( (val % 3600)/60 ),
      sec: Math.floor( val % 60 )
    });
  }

  // 스타트 버튼 이벤트
  timerStart = (e) => {
    this.setState({
    });
  }

  render() {
    return (
      <Fragment>
        {this.state.hour} : {this.state.min} : {this.state.sec} : {this.state.miliSec}
        <div className="input-row">
          <input type="text" value={this.state.inputTime} placeholder='0' onChange={this.timeChange} />
        </div>
        <div className="btn-row">
          <button className="start" onClick={this.timerStart}>start</button>
          <button className="reset" onClick={this.timerReset}>reset</button>
        </div>
      </Fragment>
    );
  }
}

export default TimeSet;
